    setCartIsShown(true);
  }

  const hideCartHandler = () =>{
    setCartIsShown(false);
  }

  return (
    <Fragment>
      <Header />
      {cartIsShown && <Cart/>}
      <main>
        <Meals/>
      </main>
    </Fragment>
  );
}

export default App;
